document.addEventListener("DOMContentLoaded",function(){var e=document.querySelector(".reply-button"),s=document.querySelector(".reply");e.addEventListener("click",function(){s.classList.contains("is-hidden")?(console.log("showing reply box"),s.classList.remove("is-hidden"),s.classList.add("is-visible")):(console.log("hiding reply box"),s.classList.remove("is-visible"),s.classList.add("is-hidden"))})});